        break;

      // Connect
      if (connect (lfd, &clientaddr, clen) < 0)
        {
          perror ("Could not connect");
          exit (42);
        }

      printf ("Data arrived\n");

      // Spawn a child to handle this packet
      switch (fork ())
        {
        case -1:
          perror ("Couldn't spawn child to handle connection");
          exit (42);

