          // descriptors, and the run the helper 
          // application
          dup2 (lfd, 0);
          dup2 (lfd, 1);

          execl ("/bin/cat", "cat", NULL);
          perror ("Exec failed");
          exit (42);
          break;

        default:
          // Parent process
          break;
        }
    }
}

